INSERT INTO `baiviet` (`id`, `TieuDe`, `DanhMuc`, `MoTa`, `NoiDung`, `HinhAnh`, `TieuDeHinhAnh`, `NgayDang`, `TacGia`, `LuotXem`, `TrangThai`, `created_at`, `updated_at`) VALUES
(1, 'Công trường nút giao 400 tỷ đồng ở cửa ngõ thủ đô', 1, 'Nút giao vành Đai 3 kết nối cao tốc Hà Nội - Hải Phòng và quận Long Biên đã gần hoàn thiện và sẽ được thông xe vào cuối tháng 12.', 'Sau 11 tháng thi công, dự án nút giao đường vành đai 3, kết nối quận Long Biên và cao tốc Hà Nội - Hải Phòng (cách cầu Thanh Trì khoảng 200 m) đã đạt gần 90% tiến độ. Dự án được chia làm hai hợp phần, do 4 nhà thầu đảm nhiệm thi công.\r\n\r\nHợp phần một là xây đường kết nối tuyến Cổ Linh - Long Biên chui qua đường vành đai 3 để đi vào cao tốc Hải Phòng và ngược lại. Đoạn này dài 1,5 km, rộng 33 -51 m.\r\nSau 11 tháng thi công, dự án nút giao đường vành đai 3, kết nối quận Long Biên và cao tốc Hà Nội - Hải Phòng (cách cầu Thanh Trì khoảng 200 m) đã đạt gần 90% tiến độ. Dự án được chia làm hai hợp phần, do 4 nhà thầu đảm nhiệm thi công.\r\n\r\nHợp phần một là xây đường kết nối tuyến Cổ Linh - Long Biên chui qua đường vành đai 3 để đi vào cao tốc Hải Phòng và ngược lại. Đoạn này dài 1,5 km, rộng 33 -51 m.\r\nĐến ngày 4/12, hợp phần một đã thi công xong trên 90% khối lượng, nhà thầu đang thi công trải nhựa đường.\r\nNhóm công nhân đang hoàn thiện dải phân cách giữa. Dải phân cách này rộng hơn một mét, làm bằng các tấm bê tông đúc sẵn cao khoảng 50 cm. Dự kiến công việc này sẽ hoàn thiện giữa tháng 12.\r\nĐoạn còn nhiều khối lượng thi công nhất hợp phần một nằm ở gầm cầu vượt đường vành đai 3, nối từ đường Cổ Linh (Long Biên) đi cao tốc Hà Nội - Hải Phòng và ngược lại. Tại đây nhiều đống đất đá chưa được san lấp, di chuyển.', '1607133113.jpeg', 'Ảnh: VNExpress', '05/12/2020', 'Bá Đô', 7, 1, '2020-12-04 18:51:53', '2021-01-13 05:54:59'),
(2, 'Nhà nổi chống lũ giá 25 triệu đồng', 1, 'Nhà nổi chống lũ giá 25 triệu đồng', 'Sáng 4/12, ngôi nhà tự nổi chống lũ của anh Lê Trung Hiếu (quận Hoàng Mai, Hà Nội) và nhóm bạn chuyên làm từ thiện được đặt thử nghiệm thành công tại góc hồ Yên Sở, quận Hoàng Mai.\r\n\r\nSau khi đưa xuống hồ, 6 người lớn ngồi lên, nhà tự nổi không bị chòng chành, phần chân đế bê tông chìm khoảng một nửa. Ngôi nhà được thiết kế riêng cho người dân miền Trung trong mùa lũ, chia làm hai phần, chân đế và mái che. Chân đế làm bằng bê tông xốp nhẹ, rỗng ở trong, tạo thành 4 ngăn hầm, có thể chứa được 4 tấn hàng hóa. Mái che hình vòm, cao chừng 2 mét, làm bằng thép quây tôn, ngoài lợp mái tre, nứa.\r\n\r\nNhà nổi do được thiết kế bằng vật liệu bê tông xốp lắp ghép cao một mét, bên trong rỗng nên khi gặp lũ cao từ 60 cm có thể tự nổi, anh Hiếu cho biết.\r\n\r\nHai bên hông nhà nổi được thiết kế thừa ra khoảng 50 cm, có thể để thêm các vật dụng cần thiết trong gia đình và gia súc, gia cầm. Theo thiết kế, nhà nổi có thể được tháo rời các tấm bê tông, phần mái có thể cuốn gọn lại khi không sử dụng. Với các vật liệu bê tông nhẹ, tôn, thép, ước tính nhà nổi có độ bền 20-30 năm.\r\n\r\nChia sẻ về ý tưởng thiết kế ngôi nhà, anh Hiếu cho biết, hồi tháng 10 cùng nhóm bạn tới Quảng Bình đưa hàng cứu trợ, chứng kiến nhiều người khổ sở chờ cứu hộ trên mái nhà, cả nhóm suy nghĩ phải có giải pháp lâu dài cho người dân.', '1607133217.jpeg', 'Ngôi nhà nổi được đặt thử nghiệm thành công dưới hồ Yên Sở vào sáng 4/12. Ảnh: Phương Sơn', '05/12/2020', 'Phương Sơn', 0, 1, '2020-12-04 18:53:37', '2020-12-04 18:53:37'),
(3, 'TP HCM xin bắn pháo hoa 3 điểm mừng Tết Dương lịch', 1, 'UBND TP HCM xin phép Thủ tướng bắn pháo hoa tại 3 điểm: đầu hầm Thủ Thiêm, toà nhà Landmark 81 và Công viên Đầm Sen mừng Tết Dương lịch 2021.', 'Nội dung này được UBND thành phố đề cập trong văn bản gửi Bộ Văn hoá, Thể thao và Du lịch hôm 3/12, để cơ quan này xem xét, trình Thủ tướng chấp thuận. Cụ thể, thành phố dự kiến bắn pháo hoa tầm cao tại 2 điểm là đầu hầm Thủ Thiêm (quận 2) và toà nhà Landmark 81 (quận Bình Thạnh); một điểm tầm thấp tại Công viên văn hoá Đầm Sen (quận 11). Thời gian bắn 15 phút, từ 0h đến 0h15 ngày 1/1/2021; kinh phí từ nguồn xã hội hoá.\r\n\r\nTheo phương án được Sở Văn hóa và Thể thao trình, khu vực đầu hầm Thủ Thiêm bắn khoảng 1.500 quả pháo tầm cao, 30 giàn tầm thấp và 10 giàn hoả thuật; toà nhà Landmark và Công viên Central Park bắn 800 quả tầm cao, 6.200 ống tầm thấp và hoả thuật; còn ở Công viên Văn hoá Đầm Sen bắn 90 giàn tầm thấp và 30 giàn hoả thuật. Hiện, việc bắn pháo hoa thực hiện theo Nghị định 36/2009. Theo đó, vào giao thừa của Tết Nguyên đán, các thành phố trực thuộc Trung ương và Thừa Thiên Huế được bắn pháo hoa nổ tầm cao và tầm thấp, thời lượng không quá 15 phút. Các tỉnh còn lại được bắn pháo hoa nổ tầm thấp không quá 15 phút.', '1607133301.jpeg', 'Pháo hoa được bắn ở toà nhà Landmark 81 mừng thống nhất đất nước 30/4/2019. Ảnh: Quỳnh Trần', '05/12/2020', 'Hữu Công', 0, 1, '2020-12-04 18:55:01', '2020-12-04 18:55:01'),
(4, 'Thu hoạch ba kích tím', 1, 'Người dân trồng ba kích dưới tán rừng, đồi núi cho năng suất 0,5 đến 3 kg mỗi bụi, lợi nhuận cao hơn 10 lần so với cây trồng khác.', 'Sau bốn 4 năm trồng tại xã miền núi Phước Hiệp, huyện Phước Sơn, anh Anh Nguyễn Duy Thạnh (ở TP Tam Kỳ) thuê máy múc và nhân công thu hoạch ba kích tím trên diện tích gần 1.000 m2.  Ba kích được anh Thạnh trồng dưới tán cây cao su, cây sưa đỏ và đồi núi trống. Cây ba kích có dạng dây leo cuốn, thân hình trụ tròn, phân nhánh nhiều. Lá đơn nguyên, mọc đối chéo chữ thập, có cuống, phiến lá hình elip thuôn dài. Anh Thạnh đang thu hoạch 800 cây ba kích với sản lượng thu về hơn 500 kg củ. Bộ rễ của cây cắm sâu vào lòng đất 0,5 đến 1,5 m nên phải dùng máy múc đào bới để thu hoạch.\r\n\r\nBốn năm trước, anh Thạnh trồng 8.000 cây giống từ Viện công nghệ sinh học (Viện Hàn lâm Khoa học và Công nghệ Việt Nam) xen canh dưới tán rừng cao su và chuyên canh khu vực đồi núi với diện tích hơn 5.000 m2. Một bộ rễ ba kích bám chặt vào lòng đất dù máy xúc đã đào lên. Lúc này gầu máy múc rung lắc để đất vỡ ra, sau đó nhân công dùng tay kéo lên.Anh Thạnh cầm hai bụi ba kích vừa thu hoạch. \"Ba kích là cây dược liệu ít sâu bệnh, phát triển tốt dù không cần chăm sóc nhiều. Hàng năm theo định kỳ làm cỏ, bón phân cho cây\", anh nói.\r\n\r\nTheo anh Thạnh, một ha ba kích cho lợi nhuận lớn hơn so với cây trồng nông nghiệp khác như keo tràm từ 10 đến 20 lần. \"Mặt hàng này được thương lái thu mua, bán cho các cơ sở chế biến trà, cao ba kích, thuốc cổ truyền...\", anh nói.', '1607133403.jpeg', 'Ảnh: VNExpress', '05/12/2020', 'Đắc Thành', 0, 1, '2020-12-04 18:56:43', '2020-12-04 18:56:43'),
(5, 'Phương án thiết kế hai phố đi bộ mới ở Sài Gòn', 1, 'Phố đi bộ Hồ Con Rùa dự kiến tăng 2.235 m2 mảng xanh, 875 chỗ ngồi, lắp hệ thống mái che vỉa hè; phố đi bộ Nguyễn Thượng Hiền sẽ bố trí thêm bãi giữ xe.', 'Theo Đề án chỉnh trang đô thị của UBND quận 3, hai phố đi bộ Hồ Con Rùa và Nguyễn Thượng Hiền sẽ hoạt động vào ban đêm.\r\n\r\nTại Hồ Con Rùa, dự kiến tăng mảng xanh 230%, tăng chỗ ngồi 250% so với hiện hữu. Các hạng mục kiến trúc trong quần thể hồ có giá trị văn hóa, lịch sử được lắp hệ thống chiếu sáng, nhạc nước mang tính thẩm mỹ để phục vụ người dân. Vỉa hè các đường xung quanh hồ như Phạm Ngọc Thạch, Trần Cao Vân, Võ Văn Tần sẽ được xây mái che với tổng chiều dài 3.200 m.\r\n\r\nCách đó 2 km, phố đi bộ Nguyễn Thượng Hiền dài gần 600 m, sẽ giới hạn từ vòng xoay Dân Chủ đến đường Nguyễn Đình Chiểu. Với lưu lượng người ước tính 5.500 lượt mỗi ngày, 7.000 lượt cuối tuần, chính quyền quận 3 dự kiến dùng làm hai bãi đỗ xe xung quanh khu đất công tại trụ sở Công an quận 3, diện tích khoảng 2.700 m2. Đường Vườn Chuối và hẻm 416 sẽ được phân luồng giao thông thay thế cho đoạn được trưng dụng làm phố đi bộ. Theo ông Phạm Thành Kiên, Bí thư quận 3, mảng xanh tập trung và quảng trường là những hạng mục cần thiết của một đô thị trung tâm nhưng địa phương chưa có. Vì vậy, quận 3 muốn khai thác hai tuyến phố đi bộ nhằm tạo không gian sinh hoạt văn hóa, kết hợp thương mại, dịch vụ để bù đắp sự thiếu hụt này. Hai tuyến phố sẽ góp phần phát triển du lịch, hoạt động kinh tế đêm của quận. Về vấn đề giao thông, Bí thư quận 3 cho rằng, đề án xây dựng hai phố đi bộ vẫn ở giai đoạn lý thuyết, còn nhiều vấn đề cần tháo gỡ để tạo sự đồng thuận với người dân, du khách. Quận sẽ phối hợp với các sở, ngành phân luồng giao thông trong những khung giờ ban đêm để không làm ảnh hưởng đến người dân.\r\n\r\n\"Không chỉ giao thông, chúng tôi sẽ có giải pháp bảo vệ môi trường, đảm bảo trật tự theo hướng phát triển bền vững tại hai phố đi bộ này\", ông Kiên nói và cho biết đang hoàn chỉnh đề án, lập phương án thiết kế kiến trúc xin chủ trương thành phố. Chính quyền quận 3 sẽ chờ thẩm định của các sở ngành và lấy ý kiến dân trong vòng ba tháng, sau đó mới thực hiện các thủ tục để khởi công.', '1607133496.jpeg', 'Phối cảnh khu vực phố đi bộ Hồ Con Rùa. Ảnh: UBND quận 3.', '05/12/2020', 'Hà An', 0, 1, '2020-12-04 18:58:16', '2020-12-04 18:58:16'),
(6, 'Nạn nhân bị \'bắt cóc\' trở về sau 22 ngày biệt tích', 2, 'Ba tuần sau khi mất tích, Sherri Papini bất ngờ trở về bên gia đình với câu chuyện mình bị bắt cóc.', 'Chiều 2/11/2016, bà mẹ hai con Sherri Papini, 34 tuổi, được chồng Keith báo mất tích khỏi nhà riêng tại thành phố Redding, bang California.\r\n\r\nKeith kể Sherri trước đó nói sẽ đón con từ nhà trẻ sau khi đi chạy bộ. Nhưng khi chồng đi làm về, Sherri và hai con nhỏ vẫn chưa có mặt ở nhà.\r\n\r\nCảm giác có chuyện bất thường, Keith bật ứng dụng định vị và tìm được điện thoại của vợ trên con đường mòn vợ hay chạy bộ, cách nhà khoảng một dặm. Điện thoại vẫn còn cắm tai nghe, gần đó là lọn tóc vàng bị giật đứt, Keith kể.Nhận tin báo, cảnh sát mở cuộc tìm kiếm với sự trợ giúp của trực thăng và chó nghiệp vụ, nhưng không tìm thấy dấu vết gì quanh khu vực tìm thấy điện thoại của Sherri. Người thân của gia đình Papini cũng phát tờ rơi và treo thưởng cho người cung cấp thông tin, nhưng không có kết quả. Vụ mất tích của bà mẹ hai con nhận được sự quan tâm rất lớn của truyền thông và dư luận.', '1607151566.jpeg', 'Sherri Papini mất tích không dấu vết sau một buổi chiều tháng 11/2016.', '05/12/2020', 'Đắc Thành', 0, 1, '2020-12-04 23:59:26', '2020-12-04 23:59:26'),
(7, 'Nhà Trắng đề xuất gói 916 tỷ USD cứu trợ Covid-19', 2, 'Bộ trưởng Tài chính Mỹ Steven Mnuchin cho biết chính phủ đề xuất gói viện trợ Covid-19 trị giá 916 tỷ USD trong nỗ lực đạt thỏa thuận lưỡng đảng.', '\"Tôi mong muốn đạt thỏa thuận lưỡng đảng, để chúng tôi có thể cung cấp nguồn cứu trợ kinh tế quan trọng này tới người lao động Mỹ, cùng các gia đình và doanh nghiệp\", Bộ trưởng Mnuchin thông báo hôm 8/12, thêm rằng ông đã xem xét đề xuất cùng Tổng thống Donald Trump và lãnh đạo phe đa số tại Thượng viện Mitch McConnell, đồng thời trình bày với Chủ tịch Hạ viện Nancy Pelosi. Theo Mnuchin, đề xuất này bao gồm \"ngân sách dành cho chính quyền địa phương và các bang, cùng sự đảm bảo mạnh mẽ về tài chính đối với các doanh nghiệp và trường học\". Giá trị gói đề xuất của chính phủ cao hơn một chút so với mức 908 tỷ USD mà một nhóm nghị sĩ lưỡng đảng đưa ra hôm 1/12.\r\n\r\nSuốt nhiều tháng qua, phe Dân chủ và Cộng hòa tại quốc hội Mỹ cùng với chính quyền Trump không thể nhất trí một dự luật nào nhằm tiếp nối gói giải cứu trị giá 2,2 nghìn tỷ USD theo Đạo luật CARES được thông qua hồi đầu năm, giúp hỗ trợ nền kinh tế Mỹ trong bối cảnh đại dịch Covid-19 càn quét đất nước.', '1607477016.jpeg', 'Bộ trưởng Tài chính Mỹ Steven Mnuchin trong một phiên điều trần tại Washington hôm 2/12. Ảnh: AFP.', '09/12/2020', 'Ánh Ngọc (Theo AFP)', 0, 1, '2020-12-08 18:23:36', '2020-12-08 18:23:36'),
(8, 'Mỹ coi Trung Quốc là \'mối đe dọa số một\' của châu Á', 2, 'Bộ trưởng Thương mại Mỹ xem Trung Quốc là mối đe dọa chính của châu Á về kinh tế và quân sự, khi căng thẳng giữa hai nước tiếp diễn.', '\"Trung Quốc tiếp tục là thị trường tiềm năng lớn nhất, nhưng đồng thời là mối đe dọa chính cả về kinh tế và quân sự trong khu vực này\", Bộ trưởng Thương mại Mỹ Wilbur Ross nói trong bài phát biểu được ghi âm sẵn tại Hội nghị châu Á 2020 của Viện nghiên cứu Milken hôm 8/12.\r\n\r\nÔng Ross cho hay Bộ Thương mại Mỹ đã áp đặt 539 thuế quan trừng phạt, trong đó 210 loại liên quan tới Trung Quốc, dù không nêu thời gian cụ thể. Washington đã đưa \"một số lượng lớn\" công ty Trung Quốc vào danh sách đen, yêu cầu họ phải có giấy phép cho các lô hàng công nghệ nhạy cảm. Washington đồng thời cũng kêu gọi Trung Quốc tiếp tục thực hiện đầy đủ cam kết trong thỏa thuận thương mại giữa hai nước ký hồi tháng một , sau gần hai năm đàm phán. Theo thỏa thuận, chính quyền Tổng thống Trump sẽ giảm thuế xuống 7,5% đối với 110 tỷ USD hàng hóa Trung Quốc. Đổi lại, Trung Quốc cam kết mua thêm 200 tỷ USD hàng hóa và dịch vụ Mỹ trong hai năm, đồng thời đẩy nhanh phê duyệt cho các công ty Mỹ tiếp cận thị trường này và cải thiện việc bảo vệ quyền sở hữu trí tuệ.\r\n\r\nPhát biểu tại hội nghị, Bộ trưởng Ross nói rằng Trung Quốc đã thực hiện 57 cam kết về kỹ thuật và mua hơn 23 triệu USD về mặt hàng nông sản của Mỹ. \"Đó là khoảng 70% trong tổng mức thỏa thuận, nhưng đáng tiếc là họ đã mua ít hơn ở các mặt hàng khác\", ông nói.', '1607477109.jpeg', 'Quốc kỳ Trung Quốc tại thủ đô Bắc Kinh hôm 29/4/2020. Ảnh: Reuters.', '09/12/2020', 'Thanh Tâm (Theo SCMP)', 0, 1, '2020-12-08 18:25:09', '2020-12-08 18:25:09'),
(9, 'Mỹ bán 280 triệu USD vũ khí cho Đài Loan', 2, 'Chính quyền Trump đồng ý bán Hệ thống Thông tin Liên lạc Chiến trường mới trị giá 280 triệu USD cho Đài Loan.', 'Cơ quan Hợp tác An ninh Quốc phòng Mỹ ngày 7/12 thông báo cho quốc hội nước này kế hoạch bán Hệ thống Thông tin Liên lạc Chiến trường và các thiết bị liên quan trị giá 280 triệu USD cho Đài Loan. Đây là hợp đồng vũ khí thứ sáu giữa Mỹ và Đài Loan trong năm nay.\r\n\r\nTrong nhiệm kỳ của mình, chính quyền Tổng thống Mỹ Donald Trump đã tăng cường hỗ trợ Đài Loan với tổng cộng 11 thương vụ bán vũ khí.\r\n\r\nCơ quan phòng vệ Đài Loan cho biết thương vụ vũ khí mới nhất chứng tỏ Mỹ không thay đổi cam kết giúp hòn đảo tăng cường khả năng phòng thủ. \"Đài Loan và Mỹ sẽ tiếp tục củng cố quan hệ đối tác an ninh để duy trì hòa bình và ổn định ở Eo biển Đài Loan\", cơ quan này cho hay.\r\n\r\nPhát biểu tại một diễn đàn an ninh ở Đài Bắc hôm nay, lãnh đạo Đài Loan Thái Anh Văn cho hay hòn đảo \"đã và đang phải nhận những đe dọa quân sự hàng ngày\" Chính quyền Đài Loan cũng trấn an người dân rằng chính quyền mới của Tổng thống đắc cử Joe Biden, thuộc đảng Dân chủ, sẽ không giảm sự ủng hộ của Mỹ đối với hòn đảo.\r\n\r\nKurt Campbell, nhà ngoại giao hàng đầu của Mỹ về khu vực Đông Á dưới thời tổng thống Barack Obama và hiện cố vấn cho Biden, cho biết cả đảng Dân chủ và Cộng hòa đều ủng hộ mạnh mẽ Đài Loan.\r\n\r\n\"Có rất nhiều người ở các đảng chính trị hiểu được ý nghĩa chiến lược sâu sắc và lợi ích chiến lược của chúng tôi trong việc duy trì mối quan hệ bền chặt với Đài Loan\", Campbell cho hay. Các thương vụ vũ khí giữa Mỹ và Đài Loan nhiều lần chọc giận Trung Quốc, đồng thời làm gia tăng căng thẳng giữa Bắc Kinh và Washington. Trung Quốc đã trừng phạt các công ty Mỹ liên quan và tăng cường hoạt động quân sự gần Đài Loan, gồm việc điều các chiến đấu cơ áp sát hòn đảo. Trung Quốc cũng luôn xem Đài Loan là một phần lãnh thổ chờ thống nhất, kể cả phải sử dụng vũ lực.', '1607477221.jpeg', 'Lãnh đạo Đài Loan Thái Anh Văn phát biểu tại một sự kiện ở Đài Bắc hồi tháng 10. Ảnh: Reuters.', '09/12/2020', 'Huyền Lê (Theo Reuters)', 0, 1, '2020-12-08 18:27:01', '2020-12-08 18:27:01'),
(10, 'Nga sắp nhận tiêm kích Su-57 sản xuất hàng loạt đầu tiên', 2, 'Tiêm kích tàng hình Su-57 đầu tiên trong dây chuyền sản xuất hàng loạt 76 chiếc sắp được bàn giao cho quân đội Nga sau một năm trì hoãn.', '\"Chúng tôi sẽ bàn giao tiêm kích Su-57 sản xuất hàng loạt đầu tiên với động cơ giai đoạn một cho quân đội Nga trước khi hết năm nay. Nó đáp ứng mọi yêu cầu về tính năng của chiến đấu cơ thế hệ 5\", giám đốc điều hành tập đoàn quốc phòng Nga Rostec Sergei Chemezov cho biết hôm 7/12.\r\n\r\nĐây là tiêm kích tàng hình Su-57 thứ hai được sản xuất hàng loạt theo đơn hàng 76 chiếc do Bộ Quốc phòng Nga đặt mua năm ngoái, nhưng là chiếc đầu tiên được bàn giao cho quân đội Nga.\r\n\r\nTập đoàn Sukhoi xuất xưởng chiếc đầu tiên trong dây chuyền sản xuất hàng loạt Su-57 vào tháng 12 năm ngoái, nhưng chưa kịp bàn giao thì gặp sự cố trong quá trình thử nghiệm, khiến phi cơ bị phá hủy hoàn toàn. Tai nạn khiến Sukhoi phải chỉnh sửa dòng Su-57 và khiến tiến độ bàn giao tiêm kích bị chậm một năm. Tiêm kích đầu tiên được bàn giao cho quân đội Nga vẫn gắn động cơ Saturn 117S phát triển từ biến thể cho chiến đấu cơ đa năng Su-35S, do động cơ Izdeliye 30 tiên tiến hơn chưa được hoàn thiện.', '1607477296.jpeg', 'Tiêm kích Su-57 Nga bay thử hồi tháng 8. Ảnh: Russian Planes', '09/12/2020', 'Vũ Anh (Theo TASS)', 0, 1, '2020-12-08 18:28:16', '2020-12-08 18:28:16'),
(11, 'Man Utd văng khỏi Champions League', 3, 'Thua Leipzig 2-3 ở lượt trận cuối bảng H, Man Utd đứng thứ ba và phải xuống chơi vòng 1/16 Europa League.', 'Đêm buồn của Man Utd bắt đầu ngay phút thứ hai. Aaron Wan-Bissaka bỏ vị trí và không lùi về kịp để kèm Angelino. Cầu thủ chạy cánh trái của Leipzig thoải mái băng lên đón đường đổi cánh từ Marcel Sabitzer, trước khi tung cú sút chìm vào góc xa, mở tỷ số.\r\n\r\nTừng thắng Leipzig 5-0 ở lượt đi, và chỉ cần hòa là đi tiếp, Man Utd không mất nhiều thời gian để tìm đường vào khung thành đội chủ nhà. Bảy phút sau bàn thua, Mason Greenwood nhận bóng thoáng bên cánh phải. Trong thế hai đánh một, tiền đạo 19 tuổi không chuyền mà đặt lòng chìm sát mép 16m50, nhưng bóng đi không đủ khó để hạ thủ thành Peter Gulacsi.\r\n\r\nBị dẫn, bỏ lỡ cơ hội ghi bàn, rồi thua thêm, đó là kịch bản Man Utd gặp nhiều hơn một lần tại Red Bull Arena. Bốn phút sau pha bỏ lỡ của Greenwood, Leipzig nhân đôi cách biệt. Vẫn bằng miếng đánh biên quen thuộc, chỉ khác lần này người dứt điểm trong cấm địa là cầu thủ chạy cánh phải Amadou Haidara. Không hậu vệ áo đen nào kèm Haidara, dù Ole Gunnar Solskjaer chủ trương gia cố hàng phòng ngự bằng cách kéo Luke Shaw vào giữa thành trung vệ thứ ba, bên cạnh Harry Maguire và Victor Lindelof.\r\n\r\nNếu ở bàn thứ hai, Leipzig cần bốn phút từ pha bỏ lỡ của đối thủ để tung đòn hồi mã thương, họ chỉ cần đúng một phút trong bàn thứ ba. Phút 68, Bruno Fernandes sút phạt dội xà ngang. Ngay trong tình huống đưa bóng lên sau đó của đội chủ nhà, họ tận dụng triệt để sai lầm chuyền hỏng trước cấm địa của Lindelof. Quả tạt từ cánh trái khẽ chạm người một cầu thủ áo trắng, khiến Maguire lỡ trớn, trước khi Justin Kluivert lẻn ra sau lưng, chích bóng qua người David De Gea. Ở phút 70, Leipzig dẫn 3-0, nhưng những thống kê không cho thấy sự áp đảo của đội bóng Đức. Họ sút ít hơn (7 so với 10), cầm bóng ít hơn (46%), chuyền kém chuẩn hơn (78% so với 81%), và chỉ có một tình huống qua người (so với 10 của đội khách). Những bàn thắng của thầy trò Julian Nagelsmann hầu như là kết quả của việc Man Utd tự thua.\r\n\r\nSai lầm của \"Quỷ đỏ\" bắt đầu ngay từ sơ đồ lúc xuất phát. Solskjaer chọn sơ đồ 3-4-1-2, với ý đồ kiềm tỏa những miếng đánh biên của Leipzig, nhưng có vẻ các học trò của ông chưa đủ thời gian làm quen với vị trí. Shaw liên tục bỏ vị trí trung vệ lệch trái. Ở bàn thua thứ hai, anh dâng quá cao và Haidara đã ghi bàn đúng ở vị trí mà Shaw đáng ra phải xuất hiện.\r\n\r\nBên cạnh cựu cầu thủ Southampton, Wan-Bissaka cũng là một điểm yếu trong hệ thống phòng ngự. Vốn chỉ mạnh ở kỹ năng phòng ngự, Wan-Bissaka không thể lên công về thủ liên tục trong sơ đồ mới. Hậu vệ phải người Anh thiếu cảm giác không gian và kỹ năng phán đoán, đến nỗi quên luôn cả vũ khí xoạc bóng sở trường. Khi bị thay ra ở phút 78, Wan-Bissaka mới có một cú xoạc, kém hơn mức trung bình 2,5 lần mỗi trận của anh mùa này. Thắng 3-2, Leipzig lách qua khe cửa hẹp để vào vòng 1/8 Champions League. Còn Man Utd, họ không thể tái hiện kỳ tích đã làm ở Turin năm 1999. Khi ấy, Juventus ghi hai bàn trong vòng 11 phút đầu trận, nhưng \"Quỷ đỏ\" thắng ngược 3-2, rồi lên ngôi vô địch. Một trong những chứng nhân của trận thắng lịch sử 21 năm trước - Solskjaer - giờ phải ngậm ngùi xuống chơi Europa League, và còn trăm nỗi lo cho trận derby Manchester vào 12/12.', '1607480124.jpeg', 'Orban làm tung lưới Man Utd ở phút 30, nhưng bị VAR xác định lỗi việt vị. Ảnh: AFP.', '09/12/2020', 'Thắng Nguyễn', 0, 1, '2020-12-08 19:15:24', '2020-12-08 19:15:24'),
(12, 'Ronaldo: \'Tôi chưa từng coi Messi là đối thủ\'', 3, 'Tiền đạo Cristiano Ronaldo nói rằng anh và Lionel Messi làm thân với nhau rất dễ dàng, chứ không coi nhau là đối thủ.', '\"Messi và tôi đã góp mặt ở các lễ trao giải thưởng cá nhân trong khoảng 13 năm qua\", Ronaldo nói với đài Tây Ban Nha Movistar tối 8/12. \"Tôi chưa từng coi cậu ấy là đối thủ, chỉ có truyền thông thêu dệt vậy thôi. Chúng tôi luôn làm thân với nhau một cách lý tưởng. Nếu hỏi Messi câu này, cậu ấy cũng nói vậy thôi\". Ronaldo lập cú đúp từ phạt đền, giúp Juventus hạ chủ nhà Barca 3-0. Chiến thắng này vừa đủ để họ chiếm đỉnh bảng G. \"Nhiệm vụ chỉ gần như bất khả thi\", Ronaldo nói. \"Mục tiêu của chúng tôi là khởi đầu dồn dập, và trong vòng 20 phút đã ghi hai bàn. Kể từ đó, chúng tôi tin có thể hoàn thành mục tiêu thắng cách biệt ba bàn. Chiến thắng này sẽ thúc đẩy sự tự tin nơi chúng tôi. Juventus cần thắng thuyết phục một đội mạnh như Barca\".\r\n\r\n\"Tôi thấy vui khi trở lại Tây Ban Nha và Catalonia\", tiền đạo Bồ Đào Nha nói thêm. \"Đội bóng nào ghi ba bàn tại Camp Nou cũng xứng đáng chiếm đỉnh bảng. Dù Barca chơi không tốt, chúng tôi vẫn tự tin hơn với thắng lợi này. Họ vẫn là đội bóng mạnh bậc nhất mà tôi từng chiến đấu\".\r\n\r\nJuventus cướp đỉnh bảng từ Barca để bước vào vòng 1/8 Champions League. Họ cùng giành 15 điểm, nhưng Ronaldo và đồng đội hơn đối đầu. Sau trận, Ronaldo và Messi nán lại sân trò chuyện một lúc. Trận đấu này có thể là lần cuối cùng họ so tài', '1607480259.jpeg', 'Messi đến bắt tay và khoác vai Ronaldo sau trận. Ảnh: AP', '09/12/2020', 'Hoàng An (theo Football-italia)', 0, 1, '2020-12-08 19:17:39', '2020-12-08 19:17:39'),
(13, 'Ronaldo giúp Juventus đè bẹp Barca', 3, 'Cú đúp của Cristiano từ phạt đền giúp Juventus thắng Barca 3-0, để giành đỉnh bảng G Champions League tối 8/12.', 'Ronaldo bị trung vệ Ronald Araujo đốn ngã trong cấm địa Barca, rồi mở tỷ số từ phạt đền ở phút 13. Bảy phút sau, siêu sao Bồ Đào Nha chạy chỗ hút theo cả hai trung vệ Barca, giúp Weston McKennie tung người vôlê cận thành ghi bàn trong tư thế trống trải. Đầu hiệp hai, trung vệ Clement Lenglet chơi bóng bằng tay trong cấm địa Barca và bị phạt. Một lần nữa Ronaldo sút 11m đánh lạc hướng thủ môn Marc-Andre Ter Stegen, ấn định thắng lợi 3-0. Lionel Messi dứt điểm 11 pha, trong đó có bảy pha bóng hướng về khung thành, nhưng không ghi bàn. Còn Ronaldo chỉ cần ba cú sút, để ghi hai bàn từ phạt đền, chấm dứt chuỗi 38 trận bất bại của Barca ở Camp Nou tại Champions League. Lần đầu tiên kể từ năm 2006, Barca mới kết thúc vòng bảng Champions League ở vị trí thứ hai.\r\n\r\nRonaldo ảnh hưởng tới cả ba bàn của Juventus, còn thủ môn Gianluigi Buffon chặn đứng cả bảy pha dứt điểm của Barca hướng khung thành. Hàng thủ theo sơ đồ 5-3-2 của Andrea Pirlo cũng chơi chắc chắn để giữ sạch lưới. Juventus cần thắng cách biệt để chiếm đỉnh bảng G, và họ làm đúng như vậy. Còn Barca rơi xuống nhì bảng, đối mặt nguy cơ chạm trán nhiều đội mạnh như Bayern Munich, Man City, Chelsea, Liverpool hay Dortmund.\r\n\r\nHàng thủ Barca nếu không cải thiện cho đến vòng loại trực tiếp từ tháng 2/2021, họ sẽ gặp rắc rối lớn. Trên sân Camp Nou tối 8/12, Juventus tạo ra liên tiếp cơ hội ở đầu trận. Ronaldo thử tài Ter Stegen, trước khi Danilo dứt điểm chệch khung thành trong gang tấc.\r\n\r\nNhững cơ hội đó chưa đủ để giúp Barca tỉnh ngộ. Trung vệ Araujo đá thay Gerard Pique, mắc lỗi dẫn tới bàn thua đầu tiên. Bị Ronaldo rê bóng qua trong cấm địa, Araujo giơ hai tay sang truy cản, và tiền đạo Juventus ngã ra ngay. Trọng tài Đức Tobias Stieler thổi phạt đền, và Ronaldo chạy đến tìm bóng sút phạt. Anh chờ thủ môn Ter Stegen đổ sang một bên, mới sút về hướng ngược lại. Tiền đạo 35 tuổi ghi bàn đầu tiên sau sáu trận gặp Messi ở Champions League.', '1607480360.png', 'Ronaldo (phải) ghi 14 bàn trong 13 trận gần nhất ở Camp Nou. Ảnh: AP', '09/12/2020', 'Hoàng An', 0, 1, '2020-12-08 19:19:20', '2020-12-08 19:19:20'),
(14, 'RB Leipzig - Man Utd: Trận chiến sống còn', 3, 'Đang đứng đầu bảng H nhưng nếu muốn vào vòng 1/8 Champions League, Man Utd phải tránh một thất bại trước Leipzig ở lượt cuối hôm nay.', '*Leipzig - Man Utd: 3h thứ Tư, 9/12 (giờ Hà Nội).\r\n\r\nMan Utd mở màn hoàn hảo tại bảng H với chiến thắng 2-1 trước PSG và 5-0 trước Leipzig. Tuy nhiên, trận thua 0-2 trên sân Istanbul Basaksehir rồi gần nhất là thất bại 1-3 khi tiếp đón PSG khiến họ đánh mất lợi thế.\r\n\r\nỞ lượt đấu cuối, Man Utd cần ít nhất một trận hoà. Họ sẽ giữ được đầu bảng nếu chiến thắng trên đất Đức, còn PSG sảy chân khi gặp bét bảng Basaksehir. Thầy trò Ole Gunnar Solskjaer đã thắng bốn trong sáu trận gần nhất trên mọi đấu trường, trong đó có chiến thắng 3-1 trước West Ham tại Ngoại hạng Anh cuối tuần qua. Cuối tuần này, Man Utd sẽ đối đầu Man City. \"Tôi nghĩ cá tính của Man Utd đang ngày càng được cải thiện\", HLV Solskjaer nói. \"Chúng tôi chờ đợi những trận đấu như thế này. Đó là truyền thống của Man Utd. Chúng tôi không bao giờ muốn những thứ dễ dàng. Tất nhiên, chúng tôi có nhìn nhận lại các trận đấu, đặc biệt chuyến làm khách tại Istanbul. Chúng tôi có thể giành ba điểm nhưng không và tự làm khó mình. Nhưng Man Utd đã như thế từ khi tôi thi đấu ở đây, từ rất lâu trước\".\r\n\r\nLeipzig tưởng như thất thế tại bảng H sau trận thua thảm Man Utd. Tuy nhiên, chiến thắng kịch tính 4-3 trên sân Basaksehir, nhờ bàn ấn định tỷ số ở phút bù giờ thứ hai của Alexander Sorloth, giúp đoàn quân của Julian Nagelsmann cân bằng điểm số với PSG và Man Utd. Họ cần thắng ở lượt đấu cuối để đi tiếp. Nếu hòa, Leipzig phải trông chờ Basaksehir đánh bại PSG. Cuối tuần qua, Leipzig chơi ấn tượng và cầm hoà Bayern 3-3 tại Budesliga. Đây mới là lần thứ ba Leipzig tham dự vòng bảng Champions League nhưng họ đã vào tới bán kết mùa trước. Thua kém Man Utd về kinh nghiệm nhưng chất lượng đội hình mà Nagelsmann sở hữu đủ để ông trả món nợ ở lượt đi. \"Chúng tôi biết là nếu bị dẫn, Man Utd có khả năng xoay chuyển tình thế. Họ đã thể hiện điều đó trong những trận gần đây. Leipzig cần đợi tiếng còi của trọng tài để chắc chắn trận đấu kết thúc. Chúng tôi cần chiến đấu đến những phút cuối cùng để giành ba điểm từ họ\", Nagelsmann nói.', '1607480433.jpeg', 'Man Utd đè bẹp Leipzig 5-0 tại Old Trafford. Ảnh: Reuters', '09/12/2020', 'Quang Huy', 0, 1, '2020-12-08 19:20:33', '2020-12-08 19:20:33'),
(15, '12 đội vào vòng 1/8 Champions League', 3, 'Loạt trận hôm 8/12 xác định thêm ba đội đi tiếp vào vòng knock-out Champions League - gồm RB Leipzig, PSG, Lazio, bên cạnh chín CLB khác đã chắc suất từ trước.', 'Tại bảng H, RB Leipzig hạ Man Utd 3-2, qua đó lách qua khe cửa hẹp, đi tiếp. Vì thua trận, Man Utd dâng luôn tấm vé đi tiếp cho PSG, dù trận đấu giữa đại diện nước Pháp với Istanbul Basaksehir bị hoãn. Dù có thua trận này, PSG vẫn đứng trên Man Utd nhờ hơn về chỉ số phụ là kết quả đối đầu. Tại bảng F, Lazio hòa Club Brugge 2-2, qua đó đoạt tấm vé còn lại của bảng này tiến vào vòng knock-out. Vé còn lại ở bảng này thuộc về Dortmund - đội chắc suất đi tiếp từ lượt trận trước và thắng chủ nhà Zenit 2-1 hôm qua để chiếm ngôi đầu.\r\n\r\nCác đội còn lại chắc chắn đi tiếp từ lượt trận trước gồm Juventus và Barca (bảng G), Bayern (bảng A), Man City và Porto (bảng C), Liverpool (bảng D), Chelsea và Sevilla (bảng E).\r\n\r\nNhững trận đấu cuối cùng của vòng bảng Champions League 2020-2021 sẽ diễn ra hôm nay 9/12, và xác định nốt bốn tấm vé đi tiếp cuối cùng.\r\n\r\nSự chú ý đổ dồn về bảng B, nơi cả bốn đội - Monchengladbach, Real Madrid, Shakhtar và Inter Milan - đều còn cơ hội vào vòng knock-out.\r\n\r\nTại bảng A, tấm vé còn lại cùng Bayern đi tiếp sẽ được quyết định sau cuộc đấu một mất một còn giữa Atletico Madrid và RB Salzburg. Atletico đang hơn đối thủ hai điểm và xếp trên, nên chỉ cần không thua là qua vòng bảng.\r\n\r\nTình trạng tương tự diễn ra ở bảng D khi Atalanta và Ajax Amsterdam sẽ quyết đấu cho vị trí nhì bảng, đứng sau Liverpool. Atalanta có lợi thế khi đang hơn đối thủ một điểm.', '1607480586.jpeg', 'Ronaldo đã ghi 12 bàn trong 10 trận trên mọi đấu trường mùa này. Ảnh: Reuters.', '09/12/2020', 'Thanh Quý (theo Opta, Goal)', 4, 1, '2020-12-08 19:23:06', '2021-12-30 06:02:05'),
(16, 'Người Việt mất 79 ngày lương để mua iPhone 12 Pro', 4, 'Các nhà phân tích của hãng nghiên cứu thị trường Picodi đã tính toán số ngày người dân của một quốc gia cụ thể phải làm việc để đủ tiền dòng iPhone cao cấp mới nhất của Apple.', 'Các nhà phân tích của Picodi đã so sánh giá của iPhone 12 Pro (128 GB) với mức lương trung bình ở nhiều quốc gia. Mức tính số tiền lương của Picodi để mua được iPhone 12 Pro được kèm điều kiện không chi tiêu một xu nào từ số tiền lương kiếm được.\r\n\r\nTại Ukraine, iPhone 12 Pro (128 GB) có giá 39.999 UAH (khoảng 1.409 USD). Theo Dịch vụ Thống kê Nhà nước, mức lương trung bình ở Ukraine là 9.658 UAH, khoảng 340 USD. Như vậy, trung bình một người Ukraine cần làm việc 87 ngày để có thể mua được iPhone 12 Pro.\r\n\r\nCác chuyên gia tại Picodi nhận định, so với mức giá của dòng iPhone cao cấp được ra mắt năm ngoái, số ngày làm việc của một người dân tại Ukraine đã giảm xuống 9,7 ngày.\r\n\r\nTại Thụy Sĩ, Mỹ và Australia, mức thu nhập cao, mỗi người chỉ cần làm việc lần lượt trong 4,4, 6,1 và 6,6 ngày là đã có thể sở hữu cho mình một chiếc iPhone sang trọng nhất của Apple. Trong khi đó, ở Đức và Ba Lan, giá thành của một chiếc iPhone 12 Pro tương đương với thu nhập trong 9,7 và 28,3 ngày làm việc của người dân tại quốc gia này.\r\n\r\nTại Việt Nam, iPhone 12 Pro phiên bản 128 GB đang có giá bán lẻ khởi điểm 30,99 triệu đồng. Theo Picodi, trung bình người Việt phải mất 79,3 ngày lương để sở hữu một chiếc iPhone 12 Pro.\r\n\r\nThông tin về mức lương trung bình được lấy từ các trang web của Bộ hoặc Cục Thống kê quốc gia và có liên quan tại thời điểm công bố giá iPhone ở từng quốc gia cụ thể.\r\n\r\nSố tiền lương sau khi trừ thuế được tính được Picodi tính toán bằng cách lấy thu nhập trung bình của cả năm chia cho 12 tháng, rồi chia tiếp cho 21 ngày công (số ngày làm việc trung bình mỗi tháng tại các quốc gia).', '1607480711.jpeg', 'Trung bình người Việt phải mất 79,3 ngày lương để sở hữu một chiếc iPhone 12 Pro.', '09/12/2020', 'Ngọc Linh / VTV', 0, 1, '2020-12-08 19:25:11', '2020-12-08 19:25:11'),
(17, 'Úc trình Quốc hội dự luật luật buộc Google, Facebook trả phí tin tức', 4, 'Dự luật này là một trong những nỗ lực để nhắm đến việc yêu cầu các công ty công nghệ trả tiền cho nội dung tin tức được sử dụng trên nền tảng của họ.', 'Theo báo cáo, tại một cuộc họp kín ở Canberra, Úc, Bộ trưởng Tài chính Josh Frydenberg đã thông báo rằng dự luật tin tức mới sẽ được đưa ra tại Quốc hội vào ngày hôm nay 9/12.\r\n\r\nĐây là một trong những nỗ lực để nhắm đến việc yêu cầu các công ty công nghệ trả tiền cho nội dung tin tức được sử dụng trên nền tảng của họ, đầu tiên là Facebook và Google. Nhiều công ty đã lên tiếng phản đối dự luật này và thậm chí khẳng định sẽ không sử dụng nội dung tin tức từ Úc. Tuy nhiên, nếu nội dung vẫn miễn phí (nghĩa là các công ty công nghệ có thể sử dụng nội dung mà không phải trả tiền) thì các công ty truyền thông cần trả tiền cho người sáng tạo nội dung. “Cả thế giới đang theo dõi những gì đang xảy ra ở Úc”, Friedenberg nói khi giới thiệu dự luật.\r\n\r\nFriedenberg cho biết YouTube và Instagram hiện nằm ngoài phạm vi của dự luật. Dự luật mới ban đầu sẽ áp dụng cho Google Search và NewsFeed của Facebook. Trong tương lai, nếu có đủ bằng chứng để chứng minh rằng những nền tảng kỹ thuật số khác sẽ gây ra sức mạnh thương lượng bất bình đẳng, các dịch vụ nền tảng này cũng có thể được đưa vào phạm vi điều chỉnh của dự luật mới.', '1607480855.jpeg', 'Ảnh: VNExpress', '09/12/2020', 'Phong Vũ', 0, 1, '2020-12-08 19:27:35', '2020-12-08 19:27:35'),
(18, 'Samsung Galaxy F62 - Mạnh mẽ chipset Exynos 9825', 4, 'Galaxy F62 có thể ra mắt vào đầu năm sau khi việc sản xuất điện thoại này đã bắt đầu.', 'Theo đó, Samsung đã bắt đầu sản xuất Galaxy F62 tại Ấn Độ. Trích dẫn các nguồn tin của mình, báo cáo nói rằng thiết bị sẽ là một trong những điện thoại mỏng nhất của thương hiệu này. Để nhớ lại, Galaxy F41 mới ra mắt gần đây có pin 6,000mAh và độ dày 8.9mm. Trong khi Galaxy M51 với pin 7,000 mAh có thân máy dày 9.5mm. Do đó, báo cáo cho thấy Samsung có thể đã tìm ra cách để cân bằng giữa cấu hình và pin hoặc có thể giảm dung lượng pin trên chiếc Galaxy F62 sắp ra mắt. Samsung được cho là đã sản xuất Galaxy F62 tại các nhà máy của họ tại Greater Noida, Ấn Độ, điện thoại này có thể xuất hiện vào khoảng quý 1 năm sau. \r\n\r\nVề cấu hình, nguồn tin đến từ Geekbench tiết lộ Galaxy F62 sẽ sử dụng chipset Exynos 9825. Đây là dòng chip vốn dành cho flagship do Samsung phát triển và trước đây nó đã xuất hiện trên thế hệ Galaxy Note 10. Chính vì vậy không quá ngạc nhiên khi mà heiuej năng của F62 tương đồng với dòng Galaxy Note 10.\r\n\r\nGalaxy F62 có RAM 6GB và chạy trên hệ điều hành Android 11 mới nhất. Ngoài ra, smartphone này còn được kỳ vọng sẽ ra mắt với màn hình AMOLED, hệ thống camera sau chất lượng, pin dung lượng cao và hỗ trợ công nghệ sạc nhanh.', '1607480985.jpeg', 'Galaxy F62 dự kiến sẽ sớm ra mắt tại Ấn Độ trong thời gian tới', '09/12/2020', 'Hoàng My (t/h)', 0, 1, '2020-12-08 19:29:45', '2020-12-08 19:29:45'),
(19, 'Apple phải nhờ Samsung giúp đỡ', 4, 'Không có lợi thế về công nghệ zoom tiềm vọng, Apple cần Samsung để nâng cấp camera tele trên iPhone.', 'Quang học gấp khúc là một công nghệ đặc biệt, có vai trò giúp những ông lớn như Samsung, Oppo hay Huawei thống trị tính năng zoom tiềm vọng trên các thiết bị cầm tay.\r\n\r\nTheo The Elec, mặc dù được đánh giá là 2 đối thủ cạnh tranh trên thị trường kinh doanh smartphone, Apple có thể phải nhờ đến sự hỗ trợ của Samsung để nâng cấp camera tele trên iPhone. Theo Phonearena, Apple không thể trực tiếp hợp tác với Samsung do đã có hợp đồng từ trước với LG. Các linh kiện cần thiết cho công nghệ này sẽ được Samsung chuyển đến LG Innotek, nhà lắp ráp camera trên iPhone. Bên cạnh đó, các bằng sáng chế liên quan đến công nghệ quang học gấp khúc không phải thế mạnh của công ty đến từ Cupertino. Kể từ khi mua lại Corephotonics, Samsung nắm giữ nhiều lợi thế về bằng sáng chế camera tele. Thậm chí, Corephotonics còn đâm đơn kiện Apple và LG do sử dụng công nghệ camera zoom tiềm vọng trên các mẫu iPhone, bắt đầu từ model iPhone 7 Plus, mà không có sự cho phép từ hãng. Dự kiến, Samsung sẽ giao các thiết bị truyền động quang học hoặc ống kính gấp khúc cho LG. Sau quá trình lắp ráp vào một module thu phóng có sẵn, camera sẽ được chuyển tới dây chuyền gia công iPhone của Foxconn.', '1607481080.jpeg', 'So với smartphone Android, camera zoom tele không phải thế mạnh trên các dòng iPhone hiện nay. Ảnh: Tuấn Anh.', '09/12/2020', 'Ngọc Phương Linh', 0, 1, '2020-12-08 19:31:20', '2020-12-08 19:31:20'),
(20, 'Bloomberg: Grab và Gojek gần hoàn tất thủ tục sáp nhập', 4, 'Nếu thành công, đây có thể là thương vụ sáp nhập công nghệ lớn nhất Đông Nam Á.', 'Thông tin về một vụ sáp nhập giữa Grab và Gojek đã được đồn đoán từ lâu. Theo Bloomberg, thương vụ này có thể sớm hoàn tất trong thời gian ngắn, khi 2 công ty đạt được những bước đàm phán quan trọng.\r\n\r\nTrích lời nguồn tin giấu tên, Bloomberg cho hay Grab và Gojek đã vượt qua được những quan điểm khác biệt, thống nhất nhiều hạng mục đàm phán để có thể tiến tới sự đồng thuận. Những chi tiết cuối cùng của thương vụ vẫn đang được cân nhắc, với sự tham gia của các cổ đông lớn như SoftBank của tỷ phú Son Masayoshi.\r\n\r\nTrao đổi với Zing, đại diện truyền thông của Grab cho biết các tin đồn này thiếu cơ sở. \"Vì vậy, phía công ty từ chối bình luận về các tin đồn này\", đại diện Grab Việt Nam cho biết. Một trong những kịch bản sau sáp nhập là nhà đồng sáng lập Grab Anthony Tan sẽ trở thành CEO của công ty mới, trong khi các lãnh đạo Gojek tiếp tục vận hành công ty dưới thương hiệu cũ tại Indonesia. Một kịch bản khác là cả 2 công ty sẽ vận hành độc lập trong một khoảng thời gian trước khi sáp nhập. Mục tiêu cuối cùng là kết hợp thành một công ty duy nhất và có thể IPO.\r\n\r\nTheo nguồn tin của Bloomberg, bước ngoặt cũng có thể xảy ra khiến thương vụ không thành công. Một trong những trở ngại lớn nhất là những quy định về độc quyền tại các quốc gia trong khu vực.\r\n\r\nGrab và Gojek đối đầu trực tiếp trong nhiều mảng kinh doanh tại Đông Nam Á. Khởi đầu từ dịch vụ gọi xe, giờ đây họ cạnh tranh cả ở mảng giao đồ ăn, thanh toán trực tuyến. Cả hai đều nhận được những khoản đầu tư lớn từ các \"cá mập\" để có thể đốt tiền, thu hút người dùng.', '1607481213.jpeg', 'Grab và Gojek là hai đối thủ lớn ở Đông Nam Á, cạnh tranh trong hàng loạt lĩnh vực. Ảnh: Nikkei Asia.', '09/12/2020', 'Hà My Theo Bloomberg', 4, 1, '2020-12-08 19:33:33', '2021-12-30 19:41:05'),
(21, 'Vì sao số nghệ sĩ Hàn mắc Covid-19 tăng nhanh?', 5, 'Nghệ sĩ vẫn gặp mặt, ăn uống trong khi hàng loạt chương trình tiếp tục được thực hiện khiến số ca nhiễm SARS-CoV-2 trong ngành giải trí Hàn Quốc ngày càng tăng.', 'Trong một tháng trở lại đây, hàng loạt ca sĩ, diễn viên và nhân viên trong ngành giải trí dương tính với virus SARS-CoV-2. Sự bùng phát của Covid-19 trong giới ca sĩ, diễn viên ảnh hưởng trực tiếp tới ngành công nghiệp giải trí, kéo theo hàng loạt cuộc kiểm tra, sàng lọc.\r\n\r\nKể từ trường hợp của nam ca sĩ Yoon Hak là nghệ sĩ đầu tiên nhiễm SARS-CoV-2, tới hiện tại, Hàn Quốc là một trong những nước có nhiều nghệ sĩ mắc Covid-19 nhất. Đầu tháng 12, Kogyeol và Bitto của nhóm nhạc nam UP10TION nhận kết quả dương tính với virus SARS-CoV-2 sau khi nam ca sĩ tiếp xúc với một bệnh nhân. Các thành viên còn lại của UP10TION nhận kết quả âm tính nhưng MC, nhân viên đài truyền hình MBC và SBS, cũng như ca sĩ xuất hiện trong cùng chương trình âm nhạc đã được đưa đi xét nghiệm. Hàng trăm thần tượng đến từ NCT, CNBLUE, aespa, AKMU, Treasure... đã trải qua các cuộc kiểm tra sức khỏe.\r\n\r\nTương tự, các ca sĩ tham gia ghi hình cho chương trình Yu Hee-yeol\'s Sketchbook của đài KBS cũng được xét nghiệm sau khi Yiren và Si Hyeon của nhóm nhạc nữ Everglow thông báo mắc Covid-19. Nhóm nhạc nữ đã quay một tập cho chương trình âm nhạc vào ngày 24/11. Gần nhất, nữ ca sĩ Chungha xét nghiệm dương tính với SARS-CoV-2 sau khi đến một trung tâm thể dục. Các thành viên của nhóm nhạc nữ TWICE, Cosmic Girls, Gugudan và DIA - những người gặp gỡ Chung Ha trước khi cô có kết quả xét nghiệm - ngay lập tức được công ty đưa đi kiểm tra. May mắn các thành viên TWICE nhận kết quả âm tính. Riêng Sana vì tiếp xúc trực tiếp với Chung Ha sẽ tự cách ly trong 14 ngày.\r\n\r\nTuy nhiên, TWICE vừa tham gia Mnet Asian Music Awards (MAMA) vào tối 6/12. Việc này dẫn đến tình huống hàng chục nhân viên thực hiện MAMA cũng được kiểm tra vì tiếp xúc với TWICE.', '1607481289.jpeg', 'Chung Ha mắc Covid-19 sau khi tới trung tâm thể dục.', '09/12/2020', 'Minh Hạo', 0, 1, '2020-12-08 19:34:49', '2020-12-08 19:34:49'),
(22, 'CÁT-XÊ CỦA CÁC RAPPER VIỆT ĐANG CAO NGẤT NGƯỞNG?', 5, 'Cơn bão rap suốt 4 tháng qua, đặc biệt là hiệu ứng từ Rap Việt và King of Rap đã đưa cát-xê của nhiều rapper Việt lên mức chưa từng có trước đây', 'Cách đây không lâu, trên trang cá nhân của một người chuyên thực hiện các sự kiện ở không gian bar, pub, club đăng tải bài viết bày tỏ phàn nàn về cát-xê tăng cao của các rapper trẻ hiện nay. Theo người này, cát-xê của các ca sĩ có những MV tiền tỷ mới đưa ra giá 50-250 triệu đồng/show. Nhưng sau khi hai game show rap là King of Rap và Rap Việt được chú ý, nhiều rapper trẻ cũng đã lấy giá như vậy. Người này cho rằng mức cát-xê này không hợp lý, nhất là khi các rapper không phải đầu tư như giới ca sĩ.\r\n\r\nChia sẻ gây tranh cãi trong giới. Nhiều người cho rằng các rapper trẻ lấy giá đến hàng trăm triệu đồng cho một show là quá cao. Trong khi, số khác khẳng định đó là mức xứng đáng. Tại một show diễn sau đó, rapper HIEUTHUHAI khẳng định mức giá đó là thật, không phải tin đồn hay bịa đặt.  “Tôi khẳng định đó là sự thật. Điều tôi muốn nói ở đây, giá cát-xê phản ánh sự đón nhận của khán giả đối với các nghệ sĩ hip hop/rap. Đây là tín hiệu tích cực từ công chúng. Tất cả nghệ sĩ đã hoạt động chăm chỉ, cực khổ suốt nhiều năm để giờ gặt hái thành quả như hiện tại nên việc đó không có vấn đề gì hết. Đó là sự tưởng thưởng xứng đáng cho nhạc Rap. Tôi hy vọng, không chỉ hiệu ứng từ các cuộc thi rap mà sẽ còn kéo dài vì giá trị thực của dòng nhạc này”, nam rapper nổi tiếng từ King of Rap nhấn mạnh. hia sẻ với Zing, đại diện một công ty chuyên tổ chức sự kiện cho biết khoảng 3 tháng nay thù lao biểu diễn của các rapper Việt tăng liên tục, thậm chí gấp rất nhiều lần so với trước.\r\n\r\n“Hồi đầu năm hay tầm này năm ngoái chỉ có Đen Vâu mới có cát-xê cao như ca sĩ hạng A nhưng thời gian gần đây nhiều rapper đã có cát-xê như vậy. Karik, Binz, Wowy hiện nay đều có cát-xê hàng trăm triệu đồng tùy chương trình”, người này nói.\r\n\r\nMột người chuyên làm PR, sự kiện ở TP.HCM cho biết cách đây 3 tháng có liên hệ với bên Binz để đặt lịch biểu diễn. Cát-xê mà phía Binz đưa ra là 300 triệu đồng. Gần đây, Binz tiếp tục tăng cát-xê, nam rapper đã bắt đầu đưa ra con số là 400 triệu đồng.\r\n\r\nNhìn chung, nhóm rapper có cát-xê tăng cao chủ yếu là những gương mặt tham gia Rap Việt và King of Rap ở vai trò giám khảo, huấn luyện viên hoặc thí sinh.\r\n\r\nMột thí sinh của King of Rap tiết lộ với phóng viên hiện nay là Pháo, RichChoi, HIEUTHUHAI của King of Rap đã có cát-xê lên tới 60-80 triệu đồng. Trong khi một số thí sinh ở Rap Việt như Dế Choắt, GDucky, Ricky Star cũng có mức cát-xê cao, thậm chí rất cao.', '1607481533.jpeg', 'Wowy hiện là rapper có cát-xê cao, theo chia sẻ của giới tổ chức sự kiện. Ảnh: Quỳnh Danh.', '09/12/2020', 'Khuê Tú', 0, 1, '2020-12-08 19:38:53', '2020-12-08 19:38:53'),
(23, 'Min trẻ trung ở tuổi 32', 5, 'Với vẻ ngoài ngày càng trẻ trung và thời thượng, người hâm mộ đùa rằng Min đã trải qua quá trình \"dậy thì thành công\" lần thứ hai.', 'Ở tuổi 32, nữ ca sĩ cũng dần rũ bỏ hình ảnh nữ tính, kín đáo và chuyển sang theo đuổi phong cách quyến rũ, trưởng thành. Khi xuất hiện trong các sản phẩm âm nhạc hay dự án hình ảnh, Min thường dùng son môi đậm, kẻ mắt sắc sảo, tạo vẻ ngoài quyến rũ. Cô lựa chọn trang phục ôm sát, cắt xẻ táo bạo. Trái ngược với phong cách trưởng thành, sắc sảo trên sân khấu, Min duy trì phong cách giản dị nhưng cá tính, trẻ trung ở ngoài đời. Gu thời trang mang hơi hướm hip-hop của nữ ca sĩ khiến nhiều người liên tưởng tới các ngôi sao ở độ tuổi tuổi 20. Min còn được khen ngợi vì có khuôn mặt Vline thon gọn, đôi mắt to tròn, sống mũi cao và khuôn miệng căng mọng. Nhìn vẻ ngoài của giọng ca Hà thành, ít người tin rằng cô đã ngoài 30 tuổi. So sánh hình ảnh cũ, nhiều ý kiến cho rằng Min trẻ hơn hẳn giai đoạn mới gia nhập Vpop. Người hâm mộ thường gọi cô là \"thánh hack tuổi\" của Vpop. Nữ ca sĩ không hay nhắc đến chủ đề tuổi tác, cô khẳng định bản thân luôn muốn mang đến cho khán giả hình ảnh tươi mới, xinh đẹp nhất. \"Hãy dùng đôi mắt để ấn định tuổi cho tôi\", Min nói với Zing.vn trong cuộc trò chuyện.So sánh hình ảnh cũ, nhiều ý kiến cho rằng Min trẻ hơn hẳn giai đoạn mới gia nhập Vpop. Người hâm mộ thường gọi cô là \"thánh hack tuổi\" của Vpop. Nữ ca sĩ không hay nhắc đến chủ đề tuổi tác, cô khẳng định bản thân luôn muốn mang đến cho khán giả hình ảnh tươi mới, xinh đẹp nhất. \"Hãy dùng đôi mắt để ấn định tuổi cho tôi\", Min nói với Zing.vn trong cuộc trò chuyện.So sánh hình ảnh cũ, nhiều ý kiến cho rằng Min trẻ hơn hẳn giai đoạn mới gia nhập Vpop. Người hâm mộ thường gọi cô là \"thánh hack tuổi\" của Vpop. Nữ ca sĩ không hay nhắc đến chủ đề tuổi tác, cô khẳng định bản thân luôn muốn mang đến cho khán giả hình ảnh tươi mới, xinh đẹp nhất. \"Hãy dùng đôi mắt để ấn định tuổi cho tôi\", Min nói với Zing.vn trong cuộc trò chuyện. So sánh hình ảnh cũ, nhiều ý kiến cho rằng Min trẻ hơn hẳn giai đoạn mới gia nhập Vpop. Người hâm mộ thường gọi cô là \"thánh hack tuổi\" của Vpop. Nữ ca sĩ không hay nhắc đến chủ đề tuổi tác, cô khẳng định bản thân luôn muốn mang đến cho khán giả hình ảnh tươi mới, xinh đẹp nhất. \"Hãy dùng đôi mắt để ấn định tuổi cho tôi\", Min nói với Zing.vn trong cuộc trò chuyện. So sánh hình ảnh cũ, nhiều ý kiến cho rằng Min trẻ hơn hẳn giai đoạn mới gia nhập Vpop. Người hâm mộ thường gọi cô là \"thánh hack tuổi\" của Vpop. Nữ ca sĩ không hay nhắc đến chủ đề tuổi tác, cô khẳng định bản thân luôn muốn mang đến cho khán giả hình ảnh tươi mới, xinh đẹp nhất. \"Hãy dùng đôi mắt để ấn định tuổi cho tôi\", Min nói với Zing.vn trong cuộc trò chuyện.', '1607481629.jpeg', 'Nhân dịp sinh nhật 7/12, Min cho ra mắt ca khúc mới Mlem Mlem với giai điệu trẻ trung, sôi động và ca từ yêu đời.', '09/12/2020', 'Tuệ Lâm', 0, 1, '2020-12-08 19:40:29', '2020-12-08 19:40:29'),
(24, 'Warner Bros. giết chết điện ảnh?', 5, 'Quyết định phát hành cùng lúc 17 phim trên HBO Max và tại các rạp của Warner Bros. có thể gây ra hậu quả nghiêm trọng cho ngành công nghiệp điện ảnh trong năm 2021.', '2020 là năm đáng quên đối với nhiều người, đặc biệt là các ông chủ rạp phim. Đại dịch Covid-19 khiến phần lớn các rạp chiếu trên toàn thế giới phải đóng cửa. Quan trọng hơn, nhiều bộ phim bom tấn bị hoãn lại đến năm 2021.\r\n\r\nCác hãng phim Hollywood đã cố gắng tìm cách thích nghi với đại dịch toàn cầu.\r\n\r\nWarner Bros. đã có câu trả lời.\r\n\r\nHãng phim thông báo có 17 bộ phim được phát hàng trong năm 2021. Đây là điều đáng mừng với người hâm mộ phim ảnh. Đáng nói, hãng sẽ phát hành song song trên HBO Max lẫn các rạp chiếu phim. Đó là dấu hiệu bất thường. Liệu cách làm này của Warner Bros. sẽ giết chết ngành công nghiệp điện ảnh? Hồi tháng 4, Universal đã phát hành Trolls World Tour trên VOD. Điều đó dẫn đến việc AMC Theatre - chuỗi rạp chiếu phim lớn nhất ở Mỹ - tuyên bố ngừng hợp tác với Universal. Trong khi đó, Disney cũng tung Mộc Lan trên Disney+. Hãng cũng làm điều tương tự với Linh hồn.\r\n\r\nNhững quyết định này đã gây chấn động trong ngành công nghiệp phim điện ảnh. Tuy nhiên, nó khác hẳn so với việc tuyên bố phát hành cùng lúc 17 dự án lớn trên dịch vụ trực tuyến và tại các cụm rạp của Warner Bros. Rạp chiếu phim vẫn là nơi để các dự án bom tấn phát triển mạnh mẽ. Sau một năm khán giả bị “bỏ đói”, 2021 sẽ là năm để khán giả giải tỏa đam mê điện ảnh. Những bộ phim bị trì hoãn như phim No Time to Die, Fast & Furious 9 cùng hàng loạt phim khác sẽ không làm người xem thất vọng.', '1607481732.jpeg', 'Hàng loạt bom tấn sẽ ra rạp trong năm 2021 sau thời gian điện ảnh đóng băng. Ảnh: Universal.', '09/12/2020', 'Trạch Dương', 0, 1, '2020-12-08 19:42:12', '2020-12-08 19:42:12'),
(25, '\'Bao Thanh Thiên\' Kim Siêu Quần chống gậy đến viếng bạn', 5, 'Ở tuổi 69, thể trạng của Kim Siêu Quần sa sút. Ông mắc nhiều căn bệnh khó chữa, đi lại khó khăn.', 'Ngày 7/12, QQ đưa tin \"Bao Thanh Thiên\" Kim Siêu Quần cùng vợ đến viếng đạo diễn quá cố Trịnh Thiếu Phong tại Nhà tang lễ thành phố Đài Bắc. Cả hai đã quen biết nhau 42 năm.\r\n\r\nTrong lần hiếm hoi xuất hiện ông được truyền thông đặc biệt chú ý. Theo QQ, nam diễn viên đi lại khó khăn, chậm chạp và phải chống gậy. So với trước đây, Kim Siêu Quần trông gầy hơn khá nhiều. Chia sẻ với truyền thông về tình hình sức khỏe, ông cho biết: \"Mấy năm nay, chân tôi ngày càng yếu, phải làm bạn với gậy. Thể trạng giảm sút khiến tôi ngại ra ngoài. Trịnh Thiếu Phong là bạn thân của tôi, vì vậy tôi phải gắng gượng đến tiễn biệt cậu ấy. Đi lại thường nhật còn khó khăn, chứ nói gì đến việc đóng phim\".\r\n\r\nCách đây vài năm, Kim Siêu Quần từng tiết lộ với truyền thông ông bị mắc hội chứng ngưng thở khi ngủ và luôn phải đeo mặt oxy vì lo ngại có thể bị suy hô hấp dẫn đến ngưng thở khi say giấc. Không chỉ vậy, nam diễn viên gạo cội còn bị cao huyết áp, rối loạn đường huyết và béo phì. Theo truyền thông Trung Quốc, sau hơn chục năm sống và làm việc ở Đại lục, hai ba năm trở lại đây, sao nam gạo cội đã về Đài Loan đoàn tụ cùng vợ. Bà xã ông là nữ diễn viên đảm nhận vai Bàng Phi, con gái Thái sư Bàng Các trong phim - Trần Kỳ. Họ không có con cái.', '1607481835.jpeg', 'Bao Thanh Thiên do Kim Siêu Quần thể hiện là vai diễn kinh điển màn ảnh, chưa ai có thể vượt mặt.', '09/12/2020', 'Di Hy', 0, 1, '2020-12-08 19:43:55', '2020-12-08 19:43:55');
